mapper.select:
select *
from t_datasource
where status = 1 and id = #{id}

mapper.insert:
update t_graph_info set
des_sql=#{item.desSql,jdbcType=VARCHAR}, graph_type = #{item.graphType,jdbcType=VARCHAR}, condition_fields = #{item.conditionFields,jdbcType=VARCHAR}, description = #{item.description,jdbcType=VARCHAR}, db_id = #{item.dbId,jdbcType=BIGINT}
id = #{item.id}
